// src/App.js
import React, { useState } from 'react';
import WeatherSearch from './components/WeatherSearch';
import WeatherResult from './components/WeatherResult';
import axios from 'axios';
import './styles.css'; // Asegúrate de tener tus estilos aquí

const App = () => {
  const [weather, setWeather] = useState(null);

  const fetchWeather = async (city) => {
    const apiKey = 'TU_API_KEY_AQUI'; // Reemplaza con tu clave de API
    const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;

    try {
      const response = await axios.get(url);
      setWeather(response.data);
    } catch (error) {
      console.error('Error fetching weather data:', error);
      setWeather(null); // Resetea si hay un error
    }
  };

  return (
    <div className="app">
      <h1>Aplicación de Clima</h1>
      <WeatherSearch onSearch={fetchWeather} />
      <WeatherResult weather={weather} />
    </div>
  );
};

export default App;
